export function defaultComparator(a, b) {
  if (a < b) {
    return -1;
  }
  if (a > b) {
    return 1;
  }
  return 0;
}
export function alphabeticComparator(a, b) {
  const {
    compare
  } = new Intl.Collator();
  return compare(a, b);
}
export function createPropComparator(propName, comparator = defaultComparator) {
  return (a, b) => comparator(a[propName], b[propName]);
}